// item-list.component.ts
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ProductService } from '../services/product.service';

@Component({
  selector: 'app-item-list',
  templateUrl: './item-list.component.html',
  styleUrls: ['./item-list.component.css']
})
export class ItemListComponent implements OnInit {
  category: string = '';
  categoryTitle: string = '';
  items: any[] = [];
  loading: boolean = false;

  constructor(
    private route: ActivatedRoute,
    private productService: ProductService
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.category = params['category'];
      this.loadItemsForCategory();
    });
  }

  loadItemsForCategory(): void {
    this.loading = true;

    // Map URL parameter to display name
    this.categoryTitle = this.getCategoryDisplayName(this.category);

    // Get items for this category
    this.productService.getProductsByCategory(this.category).subscribe({
      next: (data) => {
        this.items = data;
        this.loading = false;
      },
      error: (error) => {
        console.error('Error loading items:', error);
        this.loading = false;
        // Load mock data if API fails
        this.loadMockData();
      }
    });
  }

  getCategoryDisplayName(categoryUrl: string): string {
    const displayNames: { [key: string]: string } = {
      'sofa': 'Sofas',
      'beds': 'Beds',
      'dinning-seats': 'Dining Sets',
      'study-tables': 'Study Tables',
      'centre-tables': 'Centre Tables',
      'recliners': 'Recliners',
      'sectional-sofas': 'Sectional Sofas',
      'wardrobes': 'Wardrobes',
      'luxury-furniture': 'Luxury Furniture',
      'office-furniture': 'Office Furniture',
      'shoe-racks': 'Shoe Racks',
      'cabinets-sideboards': 'Cabinets & Sideboards'
    };

    return displayNames[categoryUrl] || categoryUrl;
  }

  loadMockData(): void {
    // Mock data for each category
    const mockData: { [key: string]: any[] } = {
      'sofa': [
        {
          id: 1,
          name: 'Modern Sofa Set',
          price: 599.99,
          imageUrl: 'https://images.unsplash.com/photo-1555041469-a586c61ea9bc?w=500&auto=format&fit=crop',
          description: 'Comfortable and stylish sofa set',
          rating: 4.5
        },
        {
          id: 2,
          name: 'Leather Sofa',
          price: 799.99,
          imageUrl: 'https://images.unsplash.com/photo-1586023492125-27b2c045efd7?w=500&auto=format&fit=crop',
          description: 'Premium leather sofa',
          rating: 4.7
        }
      ],
      'beds': [
        {
          id: 3,
          name: 'Queen Size Bed',
          price: 899.99,
          imageUrl: 'https://images.unsplash.com/photo-1505693416388-ac5ce068fe85?w=500&auto=format&fit=crop',
          description: 'Elegant queen size bed',
          rating: 4.6
        }
      ],
      'dinning-seats': [
        {
          id: 4,
          name: 'Dining Table Set',
          price: 749.99,
          imageUrl: 'https://images.unsplash.com/photo-1556228453-efd6c1ff04f6?w=500&auto=format&fit=crop',
          description: '6-seater dining set',
          rating: 4.4
        }
      ]
      // Add more mock data for other categories
    };

    this.items = mockData[this.category] || [];
  }

  // Add to cart method
  addToCart(item: any): void {
    console.log('Added to cart:', item);
    // Implement your cart logic here
  }
}
