        <a routerLink="/office">Office</a>
        <a routerLink="/bed">Beds</a>
        <a routerLink="/kitchenware">Kitchenware</a>
        <a routerLink="/lighting">Lighting</a>
      </nav>
    </div>
  </header>

  <!-- Main Content -->
  <div class="main-container">
    <!-- Hero Section -->
    <div class="hero-section">
      <h1>Premium Sofa Collection</h1>
      <p>Discover luxury and comfort with our handcrafted sofa collection. Perfect blend of style and functionality.</p>
    </div>

    <!-- Filters Section -->
    <div class="filters-section">
      <!-- Search -->
      <div class="search-container">
        <i class="fas fa-search search-icon"></i>
        <input type="text"
               class="search-input"
               placeholder="Search sofas by name, material, or color..."
               [(ngModel)]="searchTerm"
               (input)="onSearch()">
      </div>

      <!-- Categories & Sort -->
      <div class="filters-row">
        <div class="category-buttons">
          <button *ngFor="let category of categories"
                  class="category-btn"
                  [class.active]="selectedCategory === category.filter"
                  (click)="onCategorySelect(category.filter)">
            {{category.name}}
          </button>
        </div>

        <select class="sort-select" [(ngModel)]="selectedSort" (change)="onSortChange()">
          <option *ngFor="let option of sortOptions" [value]="option.value">
            {{option.name}}
          </option>
        </select>
      </div>
    </div>

    <!-- Loading State -->
    <div *ngIf="loading" class="loading-container">
      <div class="spinner"></div>
      <p>Loading sofa collection...</p>
    </div>

    <!-- Error State -->
    <div *ngIf="error && !loading" class="error-container">
      <div class="error-icon">
        <i class="fas fa-exclamation-triangle"></i>
      </div>
      <h3>Oops! Something went wrong</h3>
      <p>{{error}}</p>
      <button class="btn-cart" (click)="loadSofas()">
        <i class="fas fa-redo"></i> Try Again
      </button>
    </div>

    <!-- Sofa Grid -->
    <div *ngIf="!loading && !error" class="sofa-grid">
      <div *ngFor="let sofa of filteredSofas" class="sofa-card">
        <!-- Image Container -->
        <div class="image-container">
          <div class="sofa-image">
            <img [src]="sofa.imageUrl" [alt]="sofa.name" loading="lazy">
          </div>

          <!-- Badges -->
          <div class="badge-container">
            <span class="material-badge" [ngClass]="getBadgeClass(sofa.material)">
              <i [class]="getMaterialIcon(sofa.material)"></i>
              {{sofa.material}}
            </span>
          </div>

          <!-- Stock Status -->
          <span class="stock-badge" [ngClass]="sofa.inStock ? 'in-stock' : 'out-of-stock'">
            {{sofa.inStock ? 'In Stock' : 'Out of Stock'}}
          </span>
        </div>

        <!-- Card Content -->
        <div class="card-content">
          <h3 class="sofa-title">{{sofa.name}}</h3>
          <p class="sofa-description">{{sofa.description}}</p>

          <!-- Specifications -->
          <div class="sofa-specs">
            <div class="spec-item">
              <span class="spec-label">Color</span>
              <span class="spec-value">{{sofa.color}}</span>
            </div>
            <div class="spec-item">
              <span class="spec-label">Dimensions</span>
              <span class="spec-value">{{sofa.dimensions}}</span>
            </div>
            <div class="spec-item">
              <span class="spec-label">Added</span>
              <span class="spec-value">{{sofa.createdAt | date:'MMM d, y'}}</span>
            </div>
          </div>

          <!-- Price and Actions -->
          <div class="price-section">
            <span class="sofa-price">{{formatPrice(sofa.price)}}</span>
            <span class="discount-badge">15% OFF</span>
          </div>

          <div class="action-buttons">
            <button class="btn-cart" [disabled]="!sofa.inStock">
              <i class="fas fa-shopping-cart"></i>
              {{sofa.inStock ? 'Add to Cart' : 'Out of Stock'}}
            </button>
            <button class="btn-wishlist">
              <i class="fas fa-heart"></i>
            </button>
          </div>
        </div>
      </div>
    </div>

    <!-- Empty State -->
    <div *ngIf="!loading && !error && filteredSofas.length === 0" class="empty-state">
      <div class="empty-icon">
        <i class="fas fa-couch"></i>
      </div>
      <h3>No Sofas Found</h3>
      <p>Try adjusting your search or filter criteria</p>
      <button class="btn-cart" (click)="selectedCategory = ''; searchTerm = ''; applyFilter()">
        <i class="fas fa-redo"></i> Clear Filters
      </button>
    </div>

    <!-- Results Count -->
    <div *ngIf="!loading && !error && filteredSofas.length > 0" class="mt-4 text-center">
      <p class="text-white">
        Showing {{filteredSofas.length}} of {{sofas.length}} sofas
        <span *ngIf="selectedCategory || searchTerm">
          (filtered)
        </span>
      </p>
    </div>
  </div>
</div>
