import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'my-app';
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>VGFurniture - Flat 15% Off</title>
  <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet">
  
</head>
<body>
  
  <router-outlet></router-outlet>


  <!-- Additional Content Section -->
  <section style="padding: 80px 0; background-color: #f8f9fa;">
    <div class="container">
      <div style="text-align: center; max-width: 800px; margin: 0 auto;">
        <h2 style="font-size: 36px; color: #2c3e50; margin-bottom: 20px;">Featured Products</h2>
        <p style="color: #555; margin-bottom: 40px;">Explore our carefully curated collection of furniture that combines style, comfort, and functionality.</p>

        <div style="display: grid; grid-template-columns: repeat(auto-fit, minmax(250px, 1fr)); gap: 30px;">
          <div style="background: white; border-radius: 8px; overflow: hidden; box-shadow: 0 5px 15px rgba(0,0,0,0.05);">
            <div style="height: 200px; background-color: #e67e22; display: flex; align-items: center; justify-content: center; color: white; font-size: 24px;">
              Sofa
            </div>
            <div style="padding: 20px;">
              <h3 style="color: #2c3e50; margin-bottom: 10px;">Modern Sofa Set</h3>
              <p style="color: #555; margin-bottom: 15px;">Comfortable and stylish sofa for your living room.</p>
              <div style="color: #e67e22; font-weight: 600; font-size: 18px;">$599.99</div>
            </div>
          </div>

          <div style="background: white; border-radius: 8px; overflow: hidden; box-shadow: 0 5px 15px rgba(0,0,0,0.05);">
            <div style="height: 200px; background-color: #2c3e50; display: flex; align-items: center; justify-content: center; color: white; font-size: 24px;">
              Bed
            </div>
            <div style="padding: 20px;">
              <h3 style="color: #2c3e50; margin-bottom: 10px;">Queen Size Bed</h3>
              <p style="color: #555; margin-bottom: 15px;">Elegant bed frame with storage options.</p>
              <div style="color: #e67e22; font-weight: 600; font-size: 18px;">$899.99</div>
            </div>
          </div>

          <div style="background: white; border-radius: 8px; overflow: hidden; box-shadow: 0 5px 15px rgba(0,0,0,0.05);">
            <div style="height: 200px; background-color: #3498db; display: flex; align-items: center; justify-content: center; color: white; font-size: 24px;">
              Table
            </div>
            <div style="padding: 20px;">
              <h3 style="color: #2c3e50; margin-bottom: 10px;">Dining Table Set</h3>
              <p style="color: #555; margin-bottom: 15px;">Perfect for family dinners and gatherings.</p>
              <div style="color: #e67e22; font-weight: 600; font-size: 18px;">$749.99</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </section>

