import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-contact-us',
  templateUrl: './contact-us.component.html',
  styleUrls: ['./contact-us.component.css']
})
export class ContactUsComponent implements OnInit {

  contactForm!: FormGroup;
  isSubmitted = false;
  isLoading = false;

  // Contact information
  contactInfo = {
    phone: '+91 98765 43210',
    email: 'info@vgfurniture.com',
    address: '123 Furniture Street, Design District, Mumbai - 400001',
    hours: 'Mon-Sat: 10:00 AM - 8:00 PM | Sun: 11:00 AM - 6:00 PM'
  };

  // Services list
  services = [
    { icon: 'fa-couch', title: 'Custom Furniture', desc: 'Tailored to your space and style' },
    { icon: 'fa-truck', title: 'Free Delivery', desc: 'Within 50km radius' },
    { icon: 'fa-screwdriver-wrench', title: 'Installation', desc: 'Professional assembly service' },
    { icon: 'fa-shield-alt', title: '5-Year Warranty', desc: 'On all premium products' },
    { icon: 'fa-undo', title: '30-Day Return', desc: 'Hassle-free returns' },
    { icon: 'fa-paint-roller', title: 'Interior Consultation', desc: 'Free design consultation' }
  ];

  // FAQ Section
  faqs = [
    {
      question: 'What are your delivery charges?',
      answer: 'We offer free delivery within 50km. Beyond that, charges are calculated based on distance.',
      open: false
    },
    {
      question: 'Do you provide installation services?',
      answer: 'Yes, professional installation is available for all our furniture at an additional charge.',
      open: false
    },
    {
      question: 'What is your return policy?',
      answer: 'You can return any item within 30 days of delivery in its original condition.',
      open: false
    },
    {
      question: 'Can I customize furniture dimensions?',
      answer: 'Yes, we offer custom sizing for most furniture items. Contact us for a quote.',
      open: false
    }
  ];

  constructor(private fb: FormBuilder) { }

  ngOnInit(): void {
    this.initializeForm();
  }

  initializeForm(): void {
    this.contactForm = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(3)]],
      email: ['', [Validators.required, Validators.email]],
      phone: ['', [Validators.required, Validators.pattern(/^[0-9]{10}$/)]],
      subject: ['', [Validators.required]],
      message: ['', [Validators.required, Validators.minLength(20)]],
      contactMethod: ['email', [Validators.required]]
    });
  }

  // Form submission
  onSubmit(): void {
    if (this.contactForm.valid) {
      this.isLoading = true;
      this.isSubmitted = false;

      // Simulate API call
      setTimeout(() => {
        console.log('Form submitted:', this.contactForm.value);
        this.isLoading = false;
        this.isSubmitted = true;
        this.contactForm.reset({
          contactMethod: 'email'
        });

        // Reset success message after 5 seconds
        setTimeout(() => {
          this.isSubmitted = false;
        }, 5000);
      }, 1500);
    } else {
      // Mark all fields as touched to show validation errors
      Object.keys(this.contactForm.controls).forEach(key => {
        this.contactForm.get(key)?.markAsTouched();
      });
    }
  }

  // Toggle FAQ
  toggleFAQ(index: number): void {
    this.faqs[index].open = !this.faqs[index].open;
  }

  // Quick contact buttons
  quickContact(method: string): void {
    if (method === 'call') {
      window.location.href = `tel:${this.contactInfo.phone}`;
    } else if (method === 'email') {
      window.location.href = `mailto:${this.contactInfo.email}`;
    } else if (method === 'whatsapp') {
      window.open(`https://wa.me/919876543210?text=Hello%20VG%20Furniture,%20I%20have%20a%20query`, '_blank');
    }
  }

  // Getter for easy form access
  get f() { return this.contactForm.controls; }
}
