import { Component, OnInit } from '@angular/core';
import { SofaService } from '../../services/sofa.service'; // Import the service
import { Sofa } from '../../Model/SofaDO';
import { catchError } from 'rxjs/operators';
import { of } from 'rxjs';

@Component({
  selector: 'app-sofa',
  templateUrl: './sofa.component.html',
  styleUrls: ['./sofa.component.css']
})
export class SofaComponent implements OnInit {
  sofaData: any;
  loading = false;
  error = '';
  SofaList: Array<Sofa> = new Array<Sofa>();

  constructor(private sofaService: SofaService) { }

  ngOnInit(): void {
    this.loadSofaData();
  }

  loadSofaData(): void {
    this.loading = true;
    this.error = '';

    this.sofaService.getSofaData()
      .pipe(
        catchError(error => {
          this.error = 'Failed to load sofa data. Please try again.';
          this.loading = false;
          return of(null);
        })
      )
      .subscribe(data => {
        if (data) {
          this.sofaData = data;
        }
        this.loading = false;
      });
  }

  // Example method to create new sofa
  createNewSofa(): void {
    const newSofa = {
      name: 'New Sofa',
      material: 'Leather',
      color: 'Black',
      price: 999.99
    };

    this.sofaService.createSofa(newSofa)
      .subscribe(
        response => {
          console.log('Sofa created:', response);
          this.loadSofaData(); // Refresh the data
        },
        error => {
          console.error('Error creating sofa:', error);
        }
      );
  }

  // Example method to update sofa
  updateSofa(id: number): void {
    const updatedData = {
      name: 'Updated Sofa Name',
      price: 1299.99
    };

    this.sofaService.updateSofa(id, updatedData)
      .subscribe(
        response => {
          console.log('Sofa updated:', response);
          this.loadSofaData(); // Refresh the data
        },
        error => {
          console.error('Error updating sofa:', error);
        }
      );
  }

  // Example method to delete sofa
  deleteSofa(id: number): void {
    if (confirm('Are you sure you want to delete this sofa?')) {
      this.sofaService.deleteSofa(id)
        .subscribe(
          response => {
            console.log('Sofa deleted:', response);
            this.loadSofaData(); // Refresh the data
          },
          error => {
            console.error('Error deleting sofa:', error);
          }
        );
    }
  }
}
<ng-container>
  <div class="row">
    <div class="col-lg-3 col-md-4 col-sm-6 col-12 mb-4" *ngFor="let sofa of SofaList">
      <div class="product-card shadow-sm border rounded h-100">
        <!-- Sofa Image -->
        <img [alt]="sofa.alt || 'Sofa Image'"
             [src]="sofa.ImageUrl || 'default-image.jpg'"
             class="img-fluid rounded-top"
             [id]="'sofa-' + sofa.ID"
             (click)="checkOutPage(sofa)" />

        <!-- Sofa Info -->
        <div class="product-info p-3">
          <!-- Title -->
          <h5 class="text-truncate" title="{{ sofa.Name || 'Unknown Sofa' }}">
            {{ sofa.Name || 'Unknown Sofa' }}
          </h5>

          <!-- Price and Discount -->
          <p class="price text-center text-success font-weight-bold my-2">
            ₹{{ sofa.Price || 0 }}
            <span class="original-price text-muted small" *ngIf="sofa.OriginalPrice && sofa.OriginalPrice > sofa.Price">
              ₹{{ sofa.OriginalPrice }}
            </span>
            <span class="discount text-danger small" *ngIf="sofa.Discount">
              ({{ sofa.Discount }}% off)
            </span>
          </p>

          <!-- Description -->
          <p *ngIf="sofa.Description" class="text-muted small">
            {{ sofa.Description }}
          </p>

          <!-- Brand Name -->
          <p *ngIf="sofa.Name" class="text-muted small">
            <strong>Brand:</strong> {{ sofa.Name }}
          </p>

          <!-- Stock Information -->
          <p *ngIf="sofa.Quantity && sofa.MaxQuantity" class="text-muted small">
            <strong>Stock:</strong> {{ sofa.Quantity }} / {{ sofa.MaxQuantity }}
          </p>
        </div>
      </div>
    </div>
  </div>
</ng-container>
