import { Component } from '@angular/core';

@Component({
  selector: 'app-contractor',
  templateUrl: './contractor.component.html',
  styleUrls: ['./contractor.component.css']
})
export class ContractorComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Join Our Team</title>
  <style>
    * {
      margin: 0;
      padding: 0;
      box-sizing: border-box;
    }

    body {
      font-family: Arial, sans-serif;
      line-height: 1.6;
    }

    /* Hero Section */
    .hero-section {
      background: linear-gradient(135deg, #a67c52 0%, #8b6542 100%);
      color: white;
      padding: 100px 20px;
      text-align: center;
    }

    .hero-content h1 {
      font-size: 3rem;
      margin-bottom: 20px;
      font-weight: bold;
    }

    .hero-content p {
      font-size: 1.3rem;
      opacity: 0.95;
    }

    /* Container */
    .container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 0 20px;
    }

    /* Introduction Section */
    .intro-section {
      padding: 60px 20px;
      background: #f8f9fa;
      text-align: center;
    }

      .intro-section h2 {
        font-size: 2.5rem;
        margin-bottom: 20px;
        color: #333;
      }

      .intro-section p {
        font-size: 1.1rem;
        color: #666;
        line-height: 1.8;
        max-width: 800px;
        margin: 0 auto;
      }

    /* Categories Section */
    .categories-section {
      padding: 60px 20px;
    }

    .category-card {
      background: white;
      border-radius: 12px;
      padding: 40px;
      margin-bottom: 40px;
      box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
      transition: all 0.3s ease;
    }

      .category-card:hover {
        box-shadow: 0 8px 16px rgba(0, 0, 0, 0.15);
        transform: translateY(-5px);
      }

      .category-card.highlighted {
        border: 3px solid #a67c52;
        box-shadow: 0 8px 20px rgba(166, 124, 82, 0.3);
      }

    .category-header {
      display: flex;
      align-items: center;
      gap: 15px;
      margin-bottom: 20px;
    }

    .category-icon {
      font-size: 3rem;
    }

    .category-header h2 {
      font-size: 2.2rem;
      color: #333;
      margin: 0;
    }

    .category-description {
      font-size: 1.2rem;
      color: #666;
      margin-bottom: 30px;
      font-style: italic;
    }

    .category-details {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: 40px;
      margin-bottom: 30px;
    }

    .requirements h3,
    .benefits h3 {
      font-size: 1.5rem;
      color: #333;
      margin-bottom: 15px;
      border-bottom: 2px solid #a67c52;
      padding-bottom: 10px;
    }

    .requirements ul,
    .benefits ul {
      list-style: none;
      padding: 0;
    }

    .requirements li,
    .benefits li {
      padding: 10px 0;
      padding-left: 30px;
      position: relative;
      color: #555;
      line-height: 1.6;
    }

      .requirements li:before {
        content: "‚úì";
        position: absolute;
        left: 0;
        color: #a67c52;
        font-weight: bold;
        font-size: 1.2rem;
      }

      .benefits li:before {
        content: "‚òÖ";
        position: absolute;
        left: 0;
        color: #ffa500;
        font-size: 1.2rem;
      }

    .apply-btn {
      background: linear-gradient(135deg, #a67c52 0%, #8b6542 100%);
      color: white;
      border: none;
      padding: 15px 40px;
      font-size: 1.1rem;
      border-radius: 8px;
      cursor: pointer;
      transition: all 0.3s ease;
      font-weight: bold;
      width: 100%;
      max-width: 300px;
      display: block;
      margin: 0 auto;
    }

      .apply-btn:hover {
        transform: translateY(-2px);
        box-shadow: 0 6px 20px rgba(166, 124, 82, 0.4);
      }

    /* Process Section */
    .process-section {
      padding: 60px 20px;
      background: #f8f9fa;
    }

      .process-section h2 {
        text-align: center;
        font-size: 2.5rem;
        margin-bottom: 50px;
        color: #333;
      }

    .process-steps {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
      gap: 30px;
    }

    .step {
      text-align: center;
      padding: 30px;
      background: white;
      border-radius: 12px;
      box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
    }

    .step-number {
      width: 60px;
      height: 60px;
      background: linear-gradient(135deg, #a67c52 0%, #8b6542 100%);
      color: white;
      border-radius: 50%;
      display: flex;
      align-items: center;
      justify-content: center;
      font-size: 1.8rem;
      font-weight: bold;
      margin: 0 auto 20px;
    }

    .step h3 {
      font-size: 1.4rem;
      color: #333;
      margin-bottom: 15px;
    }

    .step p {
      color: #666;
      line-height: 1.6;
    }

    /* Contact Section */
    .contact-section {
      padding: 60px 20px;
      text-align: center;
      background: white;
    }

      .contact-section h2 {
        font-size: 2.5rem;
        margin-bottom: 20px;
        color: #333;
      }

      .contact-section p {
        font-size: 1.2rem;
        color: #666;
      }

      .contact-section a {
        color: #a67c52;
        text-decoration: none;
        font-weight: bold;
      }

        .contact-section a:hover {
          text-decoration: underline;
        }

    /* Responsive Design */
    @media (max-width: 768px) {
      .hero-content h1 {
        font-size: 2rem;
      }

      .hero-content p {
        font-size: 1.1rem;
      }

      .category-details {
        grid-template-columns: 1fr;
        gap: 30px;
      }

      .category-header h2 {
        font-size: 1.8rem;
      }

      .process-steps {
        grid-template-columns: 1fr;
      }

      .intro-section h2,
      .process-section h2,
      .contact-section h2 {
        font-size: 2rem;
      }
    }
  </style>
</head>
<body>
  <div class="join-us-page">
    <!-- Hero Section -->
    <section class="hero-section">
      <div class="hero-content">
        <h1>Join Our Team as Contractor</h1>
        <p>Build your career with us and be part of exceptional projects</p>
      </div>
    </section>

    <!-- Introduction -->
    <section class="intro-section">
      <div class="container">
        <h2>Why Work With Us?</h2>
        <p>We're always looking for talented professionals to join our growing team. Whether you're an experienced contractor, a skilled engineer, or a craftsman carpenter, we offer competitive compensation, growth opportunities, and a supportive work environment.</p>
      </div>
    </section>

    <!-- Categories Section -->
    <section class="categories-section">
      <div class="container">

        <!-- Contractor Card -->
        <div id="contractor" class="category-card">
          <div class="category-header">
            <span class="category-icon">üèóÔ∏è</span>
            <h2>Contractor</h2>
          </div>
          <p class="category-description">Lead construction projects from start to finish</p>
          <div class="category-details">
            <div class="requirements">
              <h3>Requirements</h3>
              <ul>
                <li>Valid contractor license</li>
                <li>5+ years of construction experience</li>
                <li>Project management skills</li>
                <li>Strong leadership abilities</li>
                <li>Excellent communication skills</li>
              </ul>
