        color: var(--secondary-color);
      }

    .logo-icon {
      font-size: 24px;
      color: var(--secondary-color);
    }

    /* Modern Navigation */
    .modern-nav {
      display: flex;
      gap: 40px;
      align-items: center;
    }

    .nav-link {
      color: var(--primary-color);
      text-decoration: none;
      font-weight: 600;
      font-size: 15px;
      padding: 8px 0;
      position: relative;
      transition: var(--transition);
      text-transform: uppercase;
      letter-spacing: 0.5px;
    }

      .nav-link:hover {
        color: var(--secondary-color);
      }

      .nav-link::after {
        content: '';
        position: absolute;
        bottom: 0;
        left: 0;
        width: 0;
        height: 2px;
        background: var(--secondary-color);
        transition: var(--transition);
      }

      .nav-link:hover::after {
        width: 100%;
      }

    /* Hero Carousel - Modern Design */
    .modern-hero {
      height: 85vh;
      min-height: 600px;
      position: relative;
      overflow: hidden;
    }

    .hero-carousel {
      height: 100%;
    }

    .hero-slide {
      height: 100%;
      position: relative;
    }

    .hero-image {
      width: 100%;
      height: 100%;
      object-fit: cover;
      filter: brightness(0.7);
      transition: transform 10s ease;
    }

    .hero-slide:hover .hero-image {
      transform: scale(1.05);
    }

    .hero-content {
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      text-align: center;
      color: white;
      width: 90%;
      max-width: 900px;
      z-index: 2;
    }

    .hero-title {
      font-size: 4.5rem;
      font-weight: 800;
      margin-bottom: 20px;
      line-height: 1.1;
      text-shadow: 2px 2px 8px rgba(0, 0, 0, 0.3);
      opacity: 0;
      transform: translateY(30px);
      animation: fadeInUp 0.8s ease forwards;
    }

    .hero-subtitle {
      font-size: 1.3rem;
      margin-bottom: 40px;
      max-width: 600px;
      margin-left: auto;
      margin-right: auto;
      opacity: 0;
      transform: translateY(30px);
      animation: fadeInUp 0.8s ease 0.2s forwards;
    }

    @keyframes fadeInUp {
      to {
        opacity: 1;
        transform: translateY(0);
      }
    }

    /* Modern Button */
    .modern-btn {
      display: inline-flex;
      align-items: center;
      gap: 10px;
      background: var(--secondary-color);
      color: white;
      padding: 16px 40px;
      border-radius: 50px;
      text-decoration: none;
      font-weight: 600;
      font-size: 16px;
      transition: var(--transition);
      border: none;
      cursor: pointer;
      opacity: 0;
      transform: translateY(30px);
      animation: fadeInUp 0.8s ease 0.4s forwards;
    }

      .modern-btn:hover {
        background: #d35400;
        transform: translateY(-2px);
        box-shadow: 0 10px 20px rgba(230, 126, 34, 0.3);
      }

    /* Carousel Controls */
    .carousel-controls {
      position: absolute;
      bottom: 40px;
      left: 50%;
      transform: translateX(-50%);
      display: flex;
      gap: 15px;
      z-index: 10;
    }

    .carousel-dot {
      width: 12px;
      height: 12px;
      border-radius: 50%;
      background: rgba(255, 255, 255, 0.5);
      border: none;
      cursor: pointer;
      transition: var(--transition);
    }

      .carousel-dot.active {
        background: white;
        transform: scale(1.2);
      }

    /* Featured Products Section */
    .modern-section {
      padding: 100px 5%;
      background: var(--light-bg);
    }

    .section-header {
      text-align: center;
      margin-bottom: 60px;
    }

    .section-subtitle {
      color: var(--secondary-color);
      font-size: 14px;
      font-weight: 600;
      text-transform: uppercase;
      letter-spacing: 2px;
      margin-bottom: 15px;
    }

    .section-title {
      font-size: 2.8rem;
      color: var(--primary-color);
      font-weight: 700;
      margin-bottom: 20px;
    }

    .section-description {
      color: #666;
      font-size: 1.1rem;
      max-width: 600px;
      margin: 0 auto;
    }

    /* Modern Product Cards */
    .products-grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
      gap: 30px;
      max-width: 1400px;
      margin: 0 auto;
    }

    .product-card {
      background: white;
      border-radius: 16px;
      overflow: hidden;
      box-shadow: var(--shadow);
      transition: var(--transition);
      position: relative;
    }

      .product-card:hover {
        transform: translateY(-10px);
        box-shadow: 0 15px 30px rgba(0, 0, 0, 0.15);
      }

    .product-badge {
      position: absolute;
      top: 20px;
      right: 20px;
      background: var(--secondary-color);
      color: white;
      padding: 6px 15px;
      border-radius: 20px;
      font-size: 12px;
      font-weight: 600;
      z-index: 2;
    }

    .product-image {
      width: 100%;
      height: 250px;
      object-fit: cover;
      transition: transform 0.5s ease;
    }

    .product-card:hover .product-image {
      transform: scale(1.05);
    }

    .product-content {
      padding: 25px;
    }

    .product-category {
      color: var(--accent-color);
