  <script>
    // Modern Carousel Functionality
    document.addEventListener('DOMContentLoaded', function () {
      const slides = document.querySelectorAll('.hero-slide');
      const dots = document.querySelectorAll('.carousel-dot');
      let currentSlide = 0;
      const slideInterval = 5000; // 5 seconds

      // Function to show specific slide
      function showSlide(index) {
        // Hide all slides
        slides.forEach(slide => slide.classList.remove('active'));
        dots.forEach(dot => dot.classList.remove('active'));

        // Show selected slide
        currentSlide = index;
        slides[currentSlide].classList.add('active');
        dots[currentSlide].classList.add('active');
      }

      // Add click event to dots
      dots.forEach((dot, index) => {
        dot.addEventListener('click', () => {
          showSlide(index);
          resetAutoSlide();
        });
      });

      // Auto slide functionality
      let autoSlideInterval;

      function startAutoSlide() {
        autoSlideInterval = setInterval(() => {
          const nextSlide = (currentSlide + 1) % slides.length;
          showSlide(nextSlide);
        }, slideInterval);
      }

      function resetAutoSlide() {
        clearInterval(autoSlideInterval);
        startAutoSlide();
      }

      // Start auto sliding
      startAutoSlide();

      // Pause auto slide on hover
      const hero = document.querySelector('.modern-hero');
      hero.addEventListener('mouseenter', () => {
        clearInterval(autoSlideInterval);
      });

      hero.addEventListener('mouseleave', () => {
        startAutoSlide();
      });

      // Header scroll effect
      const header = document.querySelector('.modern-header');
      window.addEventListener('scroll', () => {
        if (window.scrollY > 100) {
          header.classList.add('scrolled');
        } else {
          header.classList.remove('scrolled');
        }
      });

      // Mobile menu toggle
      const mobileMenuBtn = document.getElementById('mobileMenuBtn');
      const modernNav = document.getElementById('modernNav');

      mobileMenuBtn.addEventListener('click', () => {
        modernNav.style.display = modernNav.style.display === 'flex' ? 'none' : 'flex';
      });

      // Initialize mobile menu
      if (window.innerWidth <= 768) {
        modernNav.style.display = 'none';
      }

      // Update on resize
      window.addEventListener('resize', () => {
        if (window.innerWidth > 768) {
          modernNav.style.display = 'flex';
        } else {
          modernNav.style.display = 'none';
        }
      });

      // Add hover effect to product cards
      const productCards = document.querySelectorAll('.product-card');
      productCards.forEach(card => {
        card.addEventListener('mouseenter', () => {
          const img = card.querySelector('.product-image');
          img.style.transform = 'scale(1.05)';
        });

        card.addEventListener('mouseleave', () => {
          const img = card.querySelector('.product-image');
          img.style.transform = 'scale(1)';
        });
      });
    });
